package com.treslines.appbase

import android.content.ActivityNotFoundException
import android.content.Context
import android.content.Intent
import android.net.Uri
import android.view.Gravity
import android.view.View
import android.view.animation.Animation
import android.view.animation.AnimationUtils
import android.widget.Toast
import androidx.annotation.AnimRes
import androidx.annotation.ArrayRes
import androidx.annotation.StringRes
import java.util.regex.Pattern

fun View.setVisible(visible: Boolean) {
    visibility = if (visible) View.VISIBLE else View.GONE
}


fun View.navigateToGooglePlayStore(context: Context, applicationId: String) {
    try {
        context.startActivity(
            Intent(
                Intent.ACTION_VIEW,
                Uri.parse("market://details?id=$applicationId")
            )
        )
    } catch (anfe: ActivityNotFoundException) {
        context.startActivity(
            Intent(
                Intent.ACTION_VIEW,
                Uri.parse("http://play.google.com/store/apps/details?id=$applicationId")
            )
        )
    }
}

fun View.showMessage(context: Context, @StringRes msg: Int) {
    val toast = Toast.makeText(context, msg, Toast.LENGTH_LONG)
    toast.setGravity(Gravity.CENTER, 0, 0)
    toast.show()
}

fun View.loadAnimation(context: Context, @AnimRes anim: Int): Animation {
    return AnimationUtils.loadAnimation(
        context,
        anim
    )
}

fun View.share(context: Context, @StringRes titleId: Int, @StringRes recommendationTextId: Int) {
    val i = Intent(Intent.ACTION_SEND)
    i.type = "text/plain"
    i.putExtra(Intent.EXTRA_SUBJECT, context.getText(R.string.app_name))
    i.putExtra(
        Intent.EXTRA_TEXT,
        context.getText(recommendationTextId)
    )
    context.startActivity(Intent.createChooser(i, context.getText(titleId)))
}

fun View.openWebPage(context: Context, url: String) {
    val webpage: Uri = Uri.parse(url)
    val intent = Intent(Intent.ACTION_VIEW, webpage)
    if (intent.resolveActivity(context.packageManager) != null) {
        context.startActivity(intent)
    }
}

/**
 *  <string-array name="emailArrayId">
 *      <item>youraddress@email.com</item>
 *  </string-array>
 */
fun View.sendEmail(subject: String, text: String, @ArrayRes emailArrayId: Int) {
    val intent = Intent(Intent.ACTION_SEND)
    intent.type = "application/octet-stream"
    intent.putExtra(Intent.EXTRA_SUBJECT, subject)
    val to: Array<String> = resources.getStringArray(emailArrayId)
    intent.putExtra(Intent.EXTRA_EMAIL, to)
    intent.putExtra(Intent.EXTRA_TEXT, text)
    context.startActivity(Intent.createChooser(intent, "Send Feedback..."))
}

